import { useState } from "react";
import "./styles.css";
import React, {Component} from "react"

class App extends React.Component {
  constructor(props) {
  super(props);
  this.state = {
    todoList: [],
    checkedState: [],
    total: 0,
    username: "",
    repos: [],
    };
}

  async componentDidMount() {
    try {
      const res = await fetch('http://127.0.0.1:8000/usernames');
      const todoList = await res.json();
      this.setState({todoList: todoList});
      this.setState({checkedState: new Array(this.state.todoList.length).fill(false)});
    } catch (e) {
      console.log(e);
  }
  }

  async getRepos(username) {
    try {
      const res = await fetch('http://127.0.0.1:8000/repos/' + username);
      const reposList = await res.json();
      this.setState({repos: reposList});
    } catch (e) {
      console.log(e);
  }
  };

  async handleOnChange(position) {
   this.setState({username: this.state.todoList[position]});
   this.getRepos(this.state.todoList[position]);
 };

  render() {
    return (
      <div className="App">
      <h3>GitHub Repository</h3>

      <ul className="toppings-list">
        <div className="username">Usernames:</div>
        {this.state.todoList.map((name, index) => {
          return (
            <li key={index}>
              <div className="toppings-list-item">
                <div className="left-section">
                  <input
                    type="button"
                    id={`custom-checkbox-${index}`}
                    name={name}
                    value={name}
                    checked={this.state.checkedState[index]}
                    onClick={() => this.handleOnChange(index)}
                  />
                </div>
              </div>
            </li>
          );
        })}
        <div className="username">Selected username:</div>
        <div className="right-section">{this.state.username}</div>
        <div className="repository_list">Repository List:</div>
        {this.state.repos.map((name, index) => {
          return (
            <li key={index}>
              <div className="repos-list-item">
                  <div>{name}</div>
              </div>
            </li>
          );
        })}
      </ul>
    </div>
    )
  }
}

export default App;
